import * as echarts from 'echarts';
import ComBar1 from '../common/CBar-1.vue';
export default {
	name: 'tabs',
	data() {
		return {
			chart0: null,
			barData: {
				labourQty: 500,
				engineeringQty: 2000,
				hardcostQty: 4600,
				softcostQty: 1000,
				financingQty: 1200
			},
			option0: {
				title: {
					text: 'Note for Bar-1 TBD'
				},
				tooltip: {
					trigger: 'axis',
					axisPointer: {
						// Use axis to trigger tooltip
						type: 'shadow' // 'shadow' as default; can also be 'line' or 'shadow'
					}
				},
				legend: {
					data: ['Actual Cost', 'Projection']
				},
				xAxis: {
					type: 'category',
					data: ['Labour', 'Engineering', 'Hard Cost', 'Soft Cost', 'Financing']
				},
				yAxis: {
					type: 'value',
					axisLabel: {
						formatter: '${value}' // Formats the y-axis labels to show a dollar sign
					}
				},
				series: [
					{
						name: 'Actual Cost',
						type: 'bar',
						data: [500, 2000, 4600, 1000, 1200],
						stack: 'total',
						label: {
							show: true
						}

					},
					{
						name: 'Projection',
						type: 'bar',
						stack: 'total',
						data: [1000, 1000, 1000, 1000, 1000],
						label: {
							show: true
						}


					}
				]
			}
		};
	},
	methods: {
		handleBarDataChange() {
			var newData = [
				this.barData.labourQty,
				this.barData.engineeringQty,
				this.barData.hardcostQty,
				this.barData.softcostQty,
				this.barData.financingQty,
				this.barData.socksQty
			];
			console.log('NEW data is:', newData);
			this.option0.series[0].data = newData;
			this.chart0.setOption(this.option0);
		},
		getData0() {
			this.chart0 = echarts.init(this.$refs.echarts0);
			this.chart0.setOption(this.option0);
		},
		resetForm(formName) {
			this.$refs[formName].resetFields();
		}

	},
	mounted() {
		this.getData0();
	},
	computed: {
		total: function () {
			return (
				parseInt(this.barData.labourQty) +
				parseInt(this.barData.financingQty) +
				parseInt(this.barData.hardcostQty) +
				parseInt(this.barData.softcostQty) +
				parseInt(this.barData.engineeringQty)
			);
			//parseInt(this.barData.labourQty+this.barData.engineeringQty+this.barData.hardcostQty+this.barData.softcostQty+this.barData.financingQty);
		}
	},
	components:{
		ComBar1
	}
};
