import mapboxgl from 'mapbox-gl';
import draggable from 'vuedraggable';

let id = 100;
export default {
	name: 'resallocate',
	data() {
		return {
			map: null,
			resname: '',
			list0: [
				{ name: 'Andy', id: 8, type: 'human' },
				{ name: 'Lucy', id: 9, type: 'human' },
				{ name: 'Mike', id: 10, type: 'human' },
				{ name: 'T11', id: 11, type: 'truck' }
			],
			list1: [
				{ name: 'John', id: 1, type: 'human' },
				{ name: 'Joao', id: 2, type: 'human' },
				{ name: 'Jean', id: 3, type: 'human' },
				{ name: 'T12', id: 4, type: 'truck' }
			],
			list2: [
				{ name: 'T54', id: 5, type: 'truck' },
				{ name: 'Edgard', id: 6, type: 'human' },
				{ name: 'T29', id: 7, type: 'truck' }
			],
			list3: [
				{ name: 'Karman', id: 15, type: 'human' },
				{ name: 'T58', id: 16, type: 'truck' },
				{ name: 'T33', id: 17, type: 'truck' }
			]
		};
	},
	mounted() {
		// Initialize the map
		mapboxgl.accessToken = 'pk.eyJ1IjoiZHowMDc3IiwiYSI6ImNsb3Z2azBndTB0ZGwyanFnNmR3NWx4NmQifQ.R2RTAvfJAI8ofToD3Ajy3A';
		this.map = new mapboxgl.Map({
			container: 'map', // container ID
			style: 'mapbox://styles/mapbox/streets-v11', // style URL
			center: [-123.13183569070522, 49.24748280844511], // starting position as [longitude, latitude]
			zoom: 9 // starting zoom
		});

		// Add a marker to the map - Richmond
		var marker1 = new mapboxgl.Marker().setLngLat([-123.121213, 49.150521]).addTo(this.map);

		// Add a marker to the map - Surrey
		var marker2 = new mapboxgl.Marker().setLngLat([-122.840154, 49.152908]).addTo(this.map);


		// Create popups for  marker1 and marker2
		var popup1 = new mapboxgl.Popup({
		  closeButton: false,
		  closeOnClick: false
		})
		  .setHTML('This is Project 1, with Data ...TBD');

		var popup2 = new mapboxgl.Popup({
		  closeButton: false,
		  closeOnClick: false
		})
		  .setHTML('This is Project 2, and Data ...TBD');

		// Set popups to appear on mouseenter and disappear on mouseleave
		marker1
		  .setPopup(popup1)
		  .on('mouseenter', function () {
		    popup1.addTo(this.map);
		  })
		  .on('mouseleave', function () {
		    popup1.remove();
		  });

		marker2
		  .setPopup(popup2)
		  .on('mouseenter', function () {
		    popup2.addTo(this.map);
		  })
		  .on('mouseleave', function () {
		    popup2.remove();
		  });
	},
	methods: {
		addPeople() {
			if (this.resname) {
				this.list0.push({ name: this.resname, id: id++, type: 'human' });
				this.resname = '';
			} else {
				this.$notify({
					title: 'Warning',
					message: 'This button will add people to the pool. Please input his/her name first.',
					type: 'warning'
				});
			}
		},
		addTruck() {
			if (this.resname) {
				this.list0.push({ name: this.resname, id: id++, type: 'truck' });
				this.resname = '';
			}else {
				this.$notify({
					title: 'Warning',
					message: 'This button will add truck to the pool. Please input truck No. first.',
					type: 'warning'
				});
			}
		},
		removeHandle(event) {
			console.log(event);
			//this.$message.success(`From ${event} moved to ${event} `);
		},
		add: function () {
			this.list.push({ name: 'Juan' });
		},
		replace: function () {
			this.list = [{ name: 'Edgard' }];
		},
		clone: function (el) {
			return {
				name: el.name + ' cloned'
			};
		},
		log: function (evt) {
			window.console.log(evt);
			// Check if 'added' is a property of myObject
			if (evt.hasOwnProperty('added')) {
				//console.log(evt.added.element.name);
				this.$message.success(`${evt.added.element.name} joined a new project!`);
			}
		}
	},
	components: {
		draggable
	}
};
