.sidebar {
	display: block;
	position: absolute;
	left: 0;
	top: 60px;
	bottom: 0;
	overflow-y: scroll;
}
.sidebar::-webkit-scrollbar {
	width: 0;
}
.sidebar-el-menu:not(.el-menu--collapse) {
	width: 250px;
}
.sidebar > ul {
	height: 100%;
}
/* 手工将icon变白色 */
.sidebar li i {
	color: #fff;
}

.arrow-wrapper {
	border-radius: 50%;
}
.collapse-btn {
	position: absolute;
	padding: 0 3px;
	top: 18px;
	right: -5px;
	z-index: 1000 !important;
	color: #4d4d4d;
	visibility: hidden;
	font-size: 0.9rem;
	background-color: #f2f2f2;
	border-radius: 50%;
}

.sidebar:hover .collapse-btn {
	visibility: visible;
}
